import React, { Component } from 'react';

class EnterAmount extends Component {
    constructor(props) {
        super(props);
        this.state = {
            amount: ""
        };
    }

    amountInput = event => {
        this.setState({
            amount: event.target.value
        })
    }

    clickEvent = () => {
        this.getAmount(this.state.amount);
    }


    render() {
        return(
            <React.Fragment>
                <form onSubmit={this.clickEvent.bind(this)}>
                    <div class="input-field col s3">
                        <label for="amount">Enter Amount</label>
                    </div>
                    <div class="input-field col s6 left">
                        <input placeholder="Amount" id="first_name" type="text" class="validate" onChange={this.amountInput}></input>
                    </div>
                    <div class="input-field col s12 center">
                    <button class="btn waves-effect waves-light" type="submit" name="action" >Submit</button>
                    </div>
                </form>
            </React.Fragment>
        )
    }
}

export default EnterAmount;
