import React, { Component } from 'react';
import EnterAmount from './EnterAmount';
import DisplayAmount from './DisplayAmount';

class Main extends Component {
    constructor(props) {
        super(props);
        this.state = {
            invalid: false,
            amount: 0,
            twoThousand: 0,
            fiveHundred: 0,
            oneHundred: 0,
            fifty: 0,
            ten: 0,
            five: 0,
            two: 0,
        }
        this.getAmount = this.getAmount.bind(this);
    }

    getAmount(amt) {
        let twothosandresult = 0;
        let fivehundredresult = 0;
        let onehundredresult = 0;
        let fiftyresult = 0;
        let tenresult = 0;
        let fiveresult = 0;
        let tworesult = 0;
        let result = 0;


        result = amt


        if ((result/2000) > 0) {
            twothosandresult = Math.floor(result/2000);
            const mul = 2000 * twothosandresult;
            result = result - mul;
        } else if ((result/500) > 0) {
            fiveHundred = Math.floor(result/500);
            const mul = 500 * twothosandresult;
            result = result - mul;
        }

        this.setState({
            twoThousand: twothosandresult,
            fiveHundred: fiveHundred
        });
    }

    render() {
        let demo;
        demo = (
            <div>
                <h3 class="card-panel center teal lighten-2">Currency Notes Calculator</h3>
                <div class="row">
                    <div class="col s6 center">
                        <EnterAmount getAmount = {this.getAmount} />
                    </div>
                    <div class="col s6 center">
                        <DisplayAmount twoThousand={this.state.twoThousand} fiveHundred={this.state.fiveHundred} />
                    </div>

                </div>
            </div>    
        )
        return(
            <div>
                {demo} 
            </div>
        )
    }
}

export default Main;
