import React, { Component } from 'react';
import EnterAmount from './EnterAmount';
import DisplayAmount from './DisplayAmount';
import CalculateAmount from './CalculateAmount';

class Main extends Component {
    constructor(props) {
        super(props);
        this.state = {
            amount: ""
        }
    }

    getAmount(amt) {
        this.setState({
            amount: amt
        })
    }

    render() {
        const {amount} = this.state;
        return(
            <div>
                <h3 class="card-panel center teal lighten-2">Currency Notes Calculator</h3>
                <div class="row">
                    <div class="col s6 center">
                        <EnterAmount getAmount = {this.getAmount} />
                    </div>
                    <div class="col s6 center">
                        <CalculateAmount renderAmount={(amount, getCurrencyInfo) => <DisplayAmount amount={amount, getCurrencyInfo} getCurrencyInfo={getCurrencyInfo} /> } />
                    </div>
                </div>
            </div>    
        )
    }
}

export default Main;
