import React, { Component } from 'react';
import EnterAmount from './EnterAmount';

class Main extends Component {
    constructor(props) {
        super(props);
        this.state = {
            amount: ""
        }

    }

    getAmount(amt) {
        this.setState({
            amount: amt
        })
    }

    render() {
        return(
            <div>
                <h3 class="card-panel center teal lighten-2">Rupees Info</h3>
                <div class="row">
                    <div class="col s6 center">
                        <EnterAmount getAmount = {this.getAmount} />
                    </div>
                    <div class="col s6 center">6-columns (one-half)</div>
                </div>
            </div>    
        )
    }
}

export default Main;
