import React, { Component } from 'react';
import EnterAmount from './EnterAmount';
import DisplayAmount from './DisplayAmount';

class Main extends Component {
    constructor(props) {
        super(props);
        this.state = {
            invalid: false,
            amount: 0,
            twoThousand: 0,
            fiveHundred: 0,
            oneHundred: 0,
            fifty: 0,
            ten: 0,
            five: 0,
            two: 0,
        }
        this.getAmount = this.getAmount.bind(this);
    }

    getAmount(amt) {
        let result = 0;

        this.setState({
            amount: amt
        });

        if ((amount/2000) > 0) {
            const rs = Math.floor(amount/2000);
            const mul = 2000 * rs;
            result = rs;
        }

        this.setState({
            twoThousand: result
        });
    }

    render() {
        let demo;
        demo = (
            <div>
                <h3 class="card-panel center teal lighten-2">Currency Notes Calculator</h3>
                <div class="row">
                    <div class="col s6 center">
                        <EnterAmount getAmount = {this.getAmount} />
                    </div>
                    <div class="col s6 center">
                        <DisplayAmount twoThousand={this.state.twoThousand} />
                    </div>

                </div>
            </div>    
        )
        return(
            <div>
                {demo} 
            </div>
        )
    }
}

export default Main;
